"use strict";$(function(){$("#btnLogin").click(function(){var t=$("#username").val(),n=$("#userpass").val();$.ajax({type:"get",url:"./iconfont/user.json",dataType:"json",success:function(e){!function(e){if(""==t||""==n)$("#messageBox").text("请输入用户名和密码");else for(var s in e){if(t!==e[s].user||n!==e[s].pwd)return $("#messageBox").text("用户名或密码错误");setCookie({key:"userid",val:t,days:1,path:"/"}),location.href="./index.html"}}(e)}})})});